import React from 'react';
import { Card } from '../components/shared/Card';

interface Tool {
    name: string;
    description: string;
    command: string;
    icon: string;
    inputs: string[];
    outputs: string[];
}

const tools: Tool[] = [
    {
        name: "testfor",
        description: "Calcule la probabilité d'une séquence d'observations avec l'algorithme Forward",
        command: "./testfor [options] fichier_modele fichier_sequence",
        icon: "fas fa-calculator",
        inputs: [
            "Fichier modèle HMM (.hmm)",
            "Fichier séquence d'observations (.seq)"
        ],
        outputs: [
            "Probabilité P(O|λ)",
            "Log-probabilité",
            "Détails du calcul"
        ]
    },
    {
        name: "testvit",
        description: "Trouve la séquence d'états la plus probable avec l'algorithme de Viterbi",
        command: "./testvit [options] fichier_modele fichier_sequence",
        icon: "fas fa-code-branch",
        inputs: [
            "Fichier modèle HMM (.hmm)",
            "Fichier séquence d'observations (.seq)"
        ],
        outputs: [
            "Séquence d'états optimale",
            "Probabilité du chemin",
            "Tracé du chemin"
        ]
    },
    {
        name: "testbw",
        description: "Entraîne un modèle HMM avec l'algorithme de Baum-Welch",
        command: "./testbw [options] fichier_modele fichier_sequence",
        icon: "fas fa-sync",
        inputs: [
            "Fichier modèle initial (.hmm)",
            "Fichier séquence d'apprentissage (.seq)"
        ],
        outputs: [
            "Modèle HMM optimisé",
            "Log de convergence",
            "Statistiques d'apprentissage"
        ]
    }
];

export const HMMTools: React.FC = () => {
    return (
        <div className="mb-5">
            <h2 className="display-5 mb-4">Outils UMDHMM</h2>

            <div className="row">
                {tools.map((tool, index) => (
                    <div key={index} className="col-md-4 mb-4">
                        <Card>
                            <h3 className="h4 mb-3">
                                <i className={`${tool.icon} me-2`}></i>
                                {tool.name}
                            </h3>
                            <p>{tool.description}</p>
                            <div className="bg-light p-2 rounded mb-3">
                                <code>{tool.command}</code>
                            </div>
                            <div className="row">
                                <div className="col-md-6">
                                    <h4 className="h6 mb-2">Entrées :</h4>
                                    <ul className="small">
                                        {tool.inputs.map((input, i) => (
                                            <li key={i}>{input}</li>
                                        ))}
                                    </ul>
                                </div>
                                <div className="col-md-6">
                                    <h4 className="h6 mb-2">Sorties :</h4>
                                    <ul className="small">
                                        {tool.outputs.map((output, i) => (
                                            <li key={i}>{output}</li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                            <button className="btn btn-primary mt-3">
                                <i className="fas fa-terminal me-2"></i>
                                Exécuter {tool.name}
                            </button>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    );
};
